// src/pages/AdminDashboard.jsx
import { useEffect, useState } from "react";
import TaskForm from "../components/TaskForm";
import TaskList from "../components/TaskList";
import NavbarAdmin from "../components/NavbarAdmin";


import { FaSignOutAlt, FaUserCircle } from "react-icons/fa";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import Swal from "sweetalert2";

const AdminTareas = () => {
  const { user, logout, token } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [formData, setFormData] = useState({
    titulo: "",
    descripcion: "",
    fecha: "",
    prioridad: "",
    categoria: "",
    usuario: "",
  });
  const [editingTaskId, setEditingTaskId] = useState(null);

  useEffect(() => {
    fetchTasks();
    fetchUsuarios();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get("/api/tasks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error("Error al obtener tareas", err);
    }
  };

  const fetchUsuarios = async () => {
    try {
      const res = await axios.get("/api/usuarios", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsuarios(res.data);
    } catch (err) {
      console.error("Error al obtener usuarios", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingTaskId) {
        await axios.put(`/api/tasks/${editingTaskId}`, formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        Swal.fire("Editada", "Tarea actualizada correctamente", "success");
      } else {
        await axios.post("/api/tasks", formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        Swal.fire("Agregada", "Tarea creada correctamente", "success");
      }

      setFormData({
        titulo: "",
        descripcion: "",
        fecha: "",
        prioridad: "",
        categoria: "",
        usuario: "",
      });
      setEditingTaskId(null);
      fetchTasks();
    } catch (err) {
      Swal.fire("Error", "No se pudo guardar la tarea", "error");
    }
  };

  const handleEdit = (task) => {
    setFormData({
      titulo: task.titulo,
      descripcion: task.descripcion,
      fecha: task.fecha?.split("T")[0] || "",
      prioridad: task.prioridad,
      categoria: task.categoria,
      usuario: task.usuario?._id || "",
    });
    setEditingTaskId(task._id);
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "¿Estás segura?",
      text: "No podrás deshacer esto",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
    });

    if (confirm.isConfirmed) {
      try {
        await axios.delete(`/api/tas/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        fetchTasks();
        Swal.fire("Eliminada", "La tarea ha sido eliminada", "success");
      } catch (err) {
        Swal.fire("Error", "No se pudo eliminar la tarea", "error");
      }
    }
  };

  const toggleEstado = async (id, estadoActual) => {
    try {
      await axios.put(
        `/api/tareas/${id}/estado`,
        { estado: !estadoActual },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchTasks();
    } catch (err) {
      console.error("Error al cambiar estado", err);
    }
  };

  const handleLogout = () => {
    logout();
  };

  return (
    <div className="container my-5">

      <div className="container">
      <NavbarAdmin />
      <TaskForm
        handleSubmit={handleSubmit}
        formData={formData}
        setFormData={setFormData}
        editingTaskId={editingTaskId}
        usuarios={usuarios}
      />


      <TaskList
        tasks={tasks}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
        toggleEstado={toggleEstado}
        isAdmin={true}
      />
      </div>
          </div>


  );
};

export default AdminTareas;
