import { useEffect, useState } from "react";
import { FaUser, FaEdit, FaTrash } from "react-icons/fa";
import NavbarAdmin from "../components/NavbarAdmin";
import { useAuth } from "../context/AuthContext";
import Swal from "sweetalert2";

const AdminUsers = () => {
  const { token } = useAuth();
  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const res = await fetch("http://localhost:5001/auth/users1", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) throw new Error("Error al obtener usuarios");

        const data = await res.json();
        setUsuarios(data);
      } catch (err) {
        console.error("Error al obtener usuarios", err);
        Swal.fire("Error", "No se pudieron obtener los usuarios", "error");
      }
    };

    fetchUsuarios();
  }, [token]);

  const handleDelete = async (userId) => {
    const confirm = await Swal.fire({
      title: "¿Estás seguro?",
      text: "Esta acción no se puede deshacer",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
      cancelButtonText: "Cancelar",
    });

    if (confirm.isConfirmed) {
      try {
        const response = await fetch(`http://localhost:5001/auth/users1/${userId}`, {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) throw new Error("Error al eliminar usuario");

        setUsuarios(usuarios.filter((usuario) => usuario._id !== userId));
        Swal.fire("Eliminado", "El usuario ha sido eliminado", "success");
      } catch (err) {
        console.error("Error al eliminar usuario", err);
        Swal.fire("Error", "No se pudo eliminar el usuario", "error");
      }
    }
  };

  const handleEdit = async (userId) => {
    const usuario = usuarios.find((u) => u._id === userId);

    const { value: formValues } = await Swal.fire({
      title: `<h4 class="fw-bold mb-3">Editar Usuario</h4>`,
      html: `
        <div class="form-group mb-3">
          <label for="swal-input1" class="form-label">Username</label>
          <input id="swal-input1" class="form-control" placeholder="Username" value="${usuario.username}" />
        </div>
        <div class="form-group mb-3">
          <label for="swal-input2" class="form-label">Email</label>
          <input id="swal-input2" class="form-control" placeholder="Email" value="${usuario.email}" />
        </div>
        <div class="form-group mb-2">
          <label for="swal-input3" class="form-label">Contraseña</label>
          <input id="swal-input3" type="password" class="form-control" placeholder="(Dejar en blanco para no cambiar)" />
        </div>
      `,
      focusConfirm: false,
      showCancelButton: true,
      confirmButtonText: '<i class="fa fa-check"></i> Guardar',
      cancelButtonText: '<i class="fa fa-times"></i> Cancelar',
      customClass: {
        confirmButton: 'btn btn-success me-2',
        cancelButton: 'btn btn-secondary',
        popup: 'shadow-lg rounded-4 px-3 py-2',
      },
      preConfirm: () => {
        const username = document.getElementById("swal-input1").value.trim();
        const email = document.getElementById("swal-input2").value.trim();
        const password = document.getElementById("swal-input3").value;

        if (!username || !email) {
          Swal.showValidationMessage("Username y Email son obligatorios");
          return false;
        }

        return { username, email, password };
      },
    });

    if (formValues) {
      try {
        const response = await fetch(`http://localhost:5001/auth/users1/${userId}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(formValues),
        });

        if (!response.ok) throw new Error("Error al actualizar usuario");

        // Actualizar localmente
        setUsuarios((prev) =>
          prev.map((u) => (u._id === userId ? { ...u, ...formValues } : u))
        );

        Swal.fire("Actualizado", "Usuario actualizado correctamente", "success");
      } catch (err) {
        console.error(err);
        Swal.fire("Error", "No se pudo actualizar el usuario", "error");
      }
    }
  };

  return (
    <div className="container">
      <NavbarAdmin />
      <div className="container my-5">
        <h2 className="text-center fw-bold mb-4">Usuarios Registrados</h2>
        <div className="list-group">
          {usuarios.map((u) => (
            <div
              key={u._id}
              className="list-group-item d-flex align-items-center gap-3"
            >
              <FaUser size={20} />
              <div className="flex-grow-1">
                <strong>{u.username}</strong> - {u.email} - Rol: {u.role}
              </div>
              <div>
                <button
                  className="btn btn-sm btn-primary ms-2"
                  onClick={() => handleEdit(u._id)}
                >
                  <FaEdit /> Editar
                </button>
                <button
                  className="btn btn-sm btn-danger ms-2"
                  onClick={() => handleDelete(u._id)}
                >
                  <FaTrash /> Eliminar
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminUsers;
