import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { Card, CardContent } from "@/components/ui/card";


const AdminUsers = () => {
  const { token } = useContext(AuthContext);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const obtenerUsuarios = async () => {
      try {
        const response = await fetch("http://localhost:5001/auth/users1", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error("Error al obtener los usuarios");
        }

        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error(error);
      }
    };

    if (token) {
      obtenerUsuarios();
    }
  }, [token]);

  return (
    <div className="container py-5">
      <h2 className="text-center mb-4">Usuarios Registrados</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {users.map((usuario) => (
          <Card key={usuario._id} className="shadow-md rounded-2xl">
            <CardContent>
              <h5 className="text-lg font-semibold">{usuario.username}</h5>
              <p className="text-sm text-muted-foreground">{usuario.email}</p>
              <p className="text-sm mt-2">
                <strong>Rol:</strong> {usuario.rol || "usuario"}
              </p>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default AdminUsers;
