// src/pages/AdminDashboard.jsx
import { useEffect, useState, useRef, useCallback } from "react";
import TaskForm from "../components/TaskForm";
import TaskList from "../components/TaskList";
import { FaSignOutAlt } from "react-icons/fa";
import { FaUserCircle } from "react-icons/fa";
import { useAuth } from "../context/AuthContext";

import axios from "axios";
import Swal from "sweetalert2";

const AdminDashboard = () => {
  const { username} = useAuth(); // Se asume que ya tienes el usuario logueado
  const [tasks, setTasks] = useState([]);
  const [formData, setFormData] = useState({
    titulo: "",
    descripcion: "",
    fecha: "",
    prioridad: "",
    categoria: "",
    usuario: "", // clave para asignar tarea
  });
  const [editingTaskId, setEditingTaskId] = useState(null);






  // Cargar todas las tareas
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get("/api/tareas/admin"); // Ruta especial para admin
      setTasks(res.data);
    } catch (err) {
      console.error("Error al obtener tareas", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingTaskId) {
        await axios.put(`/api/tareas/${editingTaskId}`, formData);
        Swal.fire("Editada", "Tarea actualizada correctamente", "success");
      } else {
        await axios.post("/api/tareas", formData);
        Swal.fire("Agregada", "Tarea creada correctamente", "success");
      }
      setFormData({
        titulo: "",
        descripcion: "",
        fecha: "",
        prioridad: "",
        categoria: "",
        usuario: "", // podrías agregar un campo de selección si quieres asignar tareas a usuarios
      });
      setEditingTaskId(null);
      fetchTasks();
    } catch (err) {
      Swal.fire("Error", "No se pudo guardar la tarea", "error");
    }
  };

  const handleEdit = (task) => {
    setFormData({
      titulo: task.titulo,
      descripcion: task.descripcion,
      fecha: task.fecha?.split("T")[0] || "",
      prioridad: task.prioridad,
      categoria: task.categoria,
      usuario: task.usuario?._id || "", // si quieres permitir cambiar el usuario
    });
    setEditingTaskId(task._id);
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "¿Estás segura?",
      text: "No podrás deshacer esto",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
    });

    if (confirm.isConfirmed) {
      try {
        await axios.delete(`/api/tareas/${id}`);
        fetchTasks();
        Swal.fire("Eliminada", "La tarea ha sido eliminada", "success");
      } catch (err) {
        Swal.fire("Error", "No se pudo eliminar la tarea", "error");
      }
    }
  };

  const toggleEstado = async (id, estadoActual) => {
    try {
      await axios.put(`/api/tareas/${id}/estado`, { estado: !estadoActual });
      fetchTasks();
    } catch (err) {
      console.error("Error al cambiar estado", err);
    }
  };

  // Dentro de AdminDashboard.jsx
const [usuarios, setUsuarios] = useState([]);

useEffect(() => {
  fetchTasks();
  fetchUsuarios();
}, []);

const fetchUsuarios = async () => {
  try {
    const res = await axios.get("/api/usuarios"); // Asegúrate de tener esta ruta
    setUsuarios(res.data);
  } catch (err) {
    console.error("Error al obtener usuarios", err);
  }
};


  return (
    <div className="container my-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h4 className="d-flex align-items-center gap-2">
          <FaUserCircle size={30}/>
          <span className="fw-semibold">Hola, {username}</span>
        </h4>


      </div>


      <h2 className="text-center fw-bold mb-4">Panel de administración</h2>

      <TaskForm
        handleSubmit={handleSubmit}
        formData={formData}
        setFormData={setFormData}
        editingTaskId={editingTaskId}
        usuarios={usuarios}
      />

      <TaskList
        tasks={tasks}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
        toggleEstado={toggleEstado}
      />
    </div>
  );
};

export default AdminDashboard;
