import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import AdminTareas from "./pages/AdminTareas";
import AdminUsers from "./pages/AdminUsers";
import { AuthProvider, useAuth } from "./context/AuthContext";
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const AppRoutes = () => {
  const { token, role, loading } = useAuth();

  if (loading) return null;

  return (
    <Routes>

<Route path="/admin/tareas" element={<AdminTareas />} />
<Route path="/admin/users" element={<AdminUsers />} />
<Route path="/register" element={<Register />} />


      <Route path="/login" element={!token ? <Login /> : <Navigate to="/" />} />
      <Route path="/register" element={token && role === "admin" ? <Register /> : <Navigate to="/login" />} />
      <Route path="/dashboard" element={token && role === "user" ? <Dashboard /> : <Navigate to="/" />} />



      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}

export default App;
