import { useEffect, useState } from "react";
import { FaUser, FaEdit, FaTrash } from "react-icons/fa";
import NavbarAdmin from "../components/NavbarAdmin";
import { useNavigate } from "react-router-dom";

const AdminUsers = () => {
    const { token } = useAuth();

  const [usuarios, setUsuarios] = useState([]);
  const navigate = useNavigate(); // Usamos useNavigate para redirigir a la edición

  // Fetch usuarios desde la API
  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const res = await fetch("http://localhost:5001/auth/users1", {
          // Asegúrate de agregar la autorización si es necesario
        });

        if (!res.ok) {
          throw new Error("Error al obtener usuarios");
        }

        const data = await res.json();
        setUsuarios(data);
      } catch (err) {
        console.error("Error al obtener usuarios", err);
      }
    };

    fetchUsuarios();
  }, []);

  // Función para eliminar usuario
  const handleDelete = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5001/auth/users1/${userId}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Error al eliminar usuario");
      }

      // Filtramos el usuario eliminado de la lista
      setUsuarios(usuarios.filter((usuario) => usuario._id !== userId));
    } catch (err) {
      console.error("Error al eliminar usuario", err);
    }
  };

  // Función para editar usuario (redirigir a la página de edición)
  const handleEdit = (userId) => {
    navigate(`/admin/users/edit/${userId}`); // Redirige a la página de edición
  };

  return (
    <div className="container">
      <NavbarAdmin />
      <div className="container my-5">
        <h2 className="text-center fw-bold mb-4">Usuarios Registrados</h2>
        <div className="list-group">
          {usuarios.map((u) => (
            <div
              key={u._id}
              className="list-group-item d-flex align-items-center gap-3"
            >
              <FaUser size={20} />
              <div className="flex-grow-1">
                <strong>{u.username}</strong> - {u.email} - Rol: {u.role}
              </div>
              <div>
                {/* Botones de acción: editar y eliminar */}
                <button
                  className="btn btn-sm btn-primary ms-2"
                  onClick={() => handleEdit(u._id)}
                >
                  <FaEdit /> Editar
                </button>
                <button
                  className="btn btn-sm btn-danger ms-2"
                  onClick={() => handleDelete(u._id)}
                >
                  <FaTrash /> Eliminar
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminUsers;
