// src/pages/AdminTareas.jsx
import { useEffect, useState, useCallback, useRef } from "react";
import TaskForm from "../components/TaskForm";
import TaskList from "../components/TaskList";
import NavbarAdmin from "../components/NavbarAdmin";
import { useAuth } from "../context/AuthContext";
import Swal from "sweetalert2";

const AdminTareas = () => {
  const { token } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [formData, setFormData] = useState({
    titulo: "",
    descripcion: "",
    fecha: "",
    prioridad: "",
    categoria: "",
    usuario: "",
  });
  const [editingTaskId, setEditingTaskId] = useState(null);
  const API_URL = "http://localhost:5001";

  // 游댷 Referencia al formulario para hacer scroll
  const formRef = useRef(null);

  const fetchTasks = useCallback(async () => {
    if (!token) return;
    try {
      const response = await fetch(`${API_URL}/tasks`, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      if (Array.isArray(data)) {
        setTasks(data);
      } else {
        console.error("Error: La respuesta no es un arreglo.");
        setTasks([]);
      }
    } catch (err) {
      console.error("Error al obtener tareas", err);
    }
  }, [token]);

  const fetchUsuarios = useCallback(async () => {
    if (!token) return;
    try {
      const response = await fetch(`${API_URL}/auth/user`, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      setUsuarios(data);
    } catch (err) {
      console.error("Error al obtener usuarios", err);
    }
  }, [token]);

  useEffect(() => {
    if (token) {
      fetchTasks();
      fetchUsuarios();
    }
  }, [fetchTasks, fetchUsuarios, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.usuario) {
      Swal.fire("Error", "Debes seleccionar un usuario para asignar la tarea", "error");
      return;
    }

    try {
      const method = editingTaskId ? "PUT" : "POST";
      const url = editingTaskId
        ? `${API_URL}/tasks/${editingTaskId}`
        : `${API_URL}/tasks`;

      const response = await fetch(url, {
        method,
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const message = editingTaskId ? "Tarea actualizada" : "Tarea creada";
        Swal.fire(message, `Tarea ${message.toLowerCase()} correctamente`, "success");
        setFormData({
          titulo: "",
          descripcion: "",
          fecha: "",
          prioridad: "",
          categoria: "",
          usuario: "",
        });
        setEditingTaskId(null);
        fetchTasks();
      } else {
        Swal.fire("Error", "No se pudo guardar la tarea", "error");
      }
    } catch (err) {
      Swal.fire("Error", "No se pudo guardar la tarea", "error");
    }
  };

  // 游댷 Funci칩n para deslizar hacia el formulario
  const scrollToForm = () => {
    if (formRef.current) {
      formRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const handleEdit = (task) => {
    setFormData({
      titulo: task.titulo,
      descripcion: task.descripcion,
      fecha: task.fecha?.split("T")[0] || "",
      prioridad: task.prioridad,
      categoria: task.categoria,
      usuario: task.usuario?._id || "",
    });
    setEditingTaskId(task._id);
    scrollToForm(); // 游댷 Deslizar hacia el formulario
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "쮼st치s segura?",
      text: "No podr치s deshacer esto",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "S칤, eliminar",
    });

    if (confirm.isConfirmed) {
      try {
        const response = await fetch(`${API_URL}/tasks/${id}`, {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (response.ok) {
          fetchTasks();
          Swal.fire("Eliminada", "La tarea ha sido eliminada", "success");
        } else {
          Swal.fire("Error", "No se pudo eliminar la tarea", "error");
        }
      } catch (err) {
        Swal.fire("Error", "No se pudo eliminar la tarea", "error");
      }
    }
  };

  const toggleEstado = async (id, estadoActual) => {
    try {
      const response = await fetch(`${API_URL}/tasks/${id}`, {
        method: "PATCH",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ estado: !estadoActual }),
      });

      if (response.ok) {
        fetchTasks();
      } else {
        console.error("Error al cambiar estado", response.statusText);
      }
    } catch (err) {
      console.error("Error al cambiar estado", err);
    }
  };

  return (
    <div className="container">
      <NavbarAdmin />
      <div className="container my-5">
        <TaskForm
          formRef={formRef} // 游댷 Pasamos la referencia al formulario
          handleSubmit={handleSubmit}
          formData={formData}
          setFormData={setFormData}
          editingTaskId={editingTaskId}
          usuarios={usuarios}
        />
        <TaskList
          tasks={Array.isArray(tasks) ? tasks : []}
          handleEdit={handleEdit}
          handleDelete={handleDelete}
          toggleEstado={toggleEstado}
          isAdmin={true}
        />
      </div>
    </div>
  );
};

export default AdminTareas;
