// src/App.jsx
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import AdminDashboard from "./pages/AdminDashboard";
import AdminPanel from "./pages/AdminPanel"
import { AuthProvider, useAuth } from "./context/AuthContext";
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';


const AppRoutes = () => {
  const { token, role, loading } = useAuth();

  if (loading) return null; // Puedes reemplazar con un spinner si deseas

  return (



    <Routes>

<Route
  path="/admin"
  element={token && role === "admin" ? <AdminPanel /> : <Navigate to="/" />}
/>
<Route
  path="/admin/usuarios"
  element={token && role === "admin" ? <AdminUsuarios /> : <Navigate to="/" />}
/>
      <Route
        path="/"
        element={
          !token
            ? <Login />
            : role === "admin"
              ? <Navigate to="/admin" />
              : <Navigate to="/dashboard" />
        }
      />
      <Route
        path="/login"
        element={
          !token
            ? <Login />
            : role === "admin"
              ? <Navigate to="/admin" />
              : <Navigate to="/dashboard" />
        }
      />
      <Route
        path="/register"
        element={
          token && role === "admin"
            ? <Register />
            : <Navigate to="/login" />
        }
      />
      <Route
        path="/dashboard"
        element={
          token && role === "user"
            ? <Dashboard />
            : <Navigate to="/" />
        }
      />
      <Route
        path="/admin"
        element={
          token && role === "admin"
            ? <AdminDashboard />  
            : <Navigate to="/" />
        }
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}

export default App;
