import { useState, useEffect } from "react";
import { FaSignOutAlt, FaUserCircle } from "react-icons/fa";
import { useAuth } from "../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";
import Swal from "sweetalert2";

const NavbarAdmin = () => {
  const { token, logout } = useAuth();
  const [username, setUsername] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate("/login"); 
      return;
    }

    fetch("http://localhost:5001/auth/user", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setUsername(data.username))
      .catch(() => Swal.fire("Error", "No se pudo obtener el usuario", "error"));
  }, [token, navigate]);

  const handleLogout = () => {
    Swal.fire({
      title: "¿Cerrar sesión?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, salir",
      cancelButtonText: "Cancelar",
    }).then((result) => {
      if (result.isConfirmed) {
        logout();
        navigate("/login");
      }
    });
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-primary px-4 py-2 mb-4">
      <div className="container-fluid d-flex justify-content-between align-items-center">
        <div className="d-flex align-items-center gap-3 text-white">
          <FaUserCircle size={24} />
          <strong>Hola, {username}</strong>
        </div>
        <div className="d-flex gap-3">
          <Link to="/admin/tareas" className="btn btn-outline-light btn-sm">Tareas</Link>
          <Link to="/admin/users" className="btn btn-outline-light btn-sm">Usuarios</Link>
          <Link to="/register" className="btn btn-outline-light btn-sm">Registrar Usuario</Link>
          <button
            className="btn btn-danger btn-sm d-flex align-items-center gap-1"
            onClick={handleLogout}
          >
            <FaSignOutAlt />
            Salir
          </button>
        </div>
      </div>
    </nav>
  );
};

export default NavbarAdmin;
