import { useEffect, useState, useRef, useCallback } from "react";
import TaskForm from "../components/TaskForm";
import TaskList from "../components/TaskList";
import Swal from "sweetalert2";
import { FaSignOutAlt, FaUserCircle } from "react-icons/fa";

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [username, setUsername] = useState("");
  const [usuarios, setUsuarios] = useState([]); // usuarios no admin
  const [formData, setFormData] = useState({
    titulo: "",
    descripcion: "",
    fecha: "",
    prioridad: "",
    categoria: "",
  });

  const [editingTaskId, setEditingTaskId] = useState(null);
  const formRef = useRef(null);

  const token = localStorage.getItem("token");
  const API_URL = "http://localhost:5001/tasks";

  // Cargar tareas
  const fetchTasks = useCallback(() => {
    fetch(API_URL, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setTasks(data))
      .catch((err) => console.error("Error cargando tareas:", err));
  }, [token]);

  // Cargar usuario y tareas
  useEffect(() => {
    if (!token) {
      window.location.href = "/login";
      return;
    }

    fetch("http://localhost:5001/auth/user", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        setUsername(data.username);

        // Si es admin, cargar usuarios no admin
        if (data.role === "admin") {
          fetch("http://localhost:5001/auth/user", {
            headers: { Authorization: `Bearer ${token}` },
          })
            .then((res) => res.json())
            .then((usuarios) => {
              const noAdmin = Array.isArray(usuarios)
                ? usuarios.filter((u) => u.role !== "admin")
                : [];
              setUsuarios(noAdmin);
            })
            .catch((err) =>
              console.error("Error al obtener usuarios no admin:", err)
            );
        }
      })
      .catch(() =>
        Swal.fire("Error", "No se pudo obtener el usuario", "error")
      );

    fetchTasks();
  }, [token, fetchTasks]);

  // Crear o editar tarea
  const handleSubmit = async (e) => {
    e.preventDefault();

    const url = editingTaskId ? `${API_URL}/${editingTaskId}` : API_URL;
    const method = editingTaskId ? "PUT" : "POST";

    try {
      const res = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (!res.ok) throw new Error("Error al guardar");

      Swal.fire(
        "Éxito",
        editingTaskId ? "Tarea actualizada" : "Tarea creada",
        "success"
      );

      setFormData({
        titulo: "",
        descripcion: "",
        fecha: "",
        prioridad: "",
        categoria: "",
      });
      setEditingTaskId(null);
      fetchTasks();
    } catch (err) {
      Swal.fire("Error", err.message, "error");
    }
  };

  // Editar tarea
  const handleEdit = (task) => {
    setFormData({
      titulo: task.titulo,
      descripcion: task.descripcion,
      fecha: task.fecha?.split("T")[0],
      prioridad: task.prioridad,
      categoria: task.categoria,
    });
    setEditingTaskId(task._id);

    if (formRef.current) {
      formRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  // Eliminar tarea
  const handleDelete = async (id) => {
    const result = await Swal.fire({
      title: "¿Estás seguro?",
      text: "No podrás revertir esta acción",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
      cancelButtonText: "Cancelar",
    });

    if (result.isConfirmed) {
      try {
        await fetch(`${API_URL}/${id}`, {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        });
        Swal.fire("Eliminado", "La tarea ha sido eliminada.", "success");
        fetchTasks();
      } catch (err) {
        Swal.fire("Error", "No se pudo eliminar la tarea", "error");
      }
    }
  };

  // Cambiar estado de la tarea
  const toggleEstado = async (id, estadoActual) => {
    try {
      await fetch(`${API_URL}/tasks/${id}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ estado: !estadoActual }),
      });
      fetchTasks();
    } catch (err) {
      console.error("Error al cambiar estado:", err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h4 className="d-flex align-items-center gap-2">
          <FaUserCircle size={30} />
          <span className="fw-semibold">Hola, {username}</span>
        </h4>

        <button
          className="btn btn-danger d-flex align-items-center gap-2 fw-bold"
          onClick={handleLogout}
          data-bs-toggle="tooltip"
          data-bs-placement="top"
          title="Cerrar sesión"
        >
          <FaSignOutAlt />
          <span>Cerrar sesión</span>
        </button>
      </div>

      <div ref={formRef}>
        <TaskForm
          formData={formData}
          setFormData={setFormData}
          handleSubmit={handleSubmit}
          editingTaskId={editingTaskId}
        />
      </div>

      <TaskList
        tasks={tasks}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
        toggleEstado={toggleEstado}
      />



    </div>
  );
};

export default Dashboard;
