// src/pages/AdminTareas.jsx
import { useEffect, useState, useCallback } from "react";
import TaskForm from "../components/TaskForm";
import TaskList from "../components/TaskList";
import NavbarAdmin from "../components/NavbarAdmin";

import { useAuth } from "../context/AuthContext";
import Swal from "sweetalert2";

const AdminTareas = () => {
  const { token } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [formData, setFormData] = useState({
    titulo: "",
    descripcion: "",
    fecha: "",
    prioridad: "",
    categoria: "",
    usuario: "",
  });
  const [editingTaskId, setEditingTaskId] = useState(null);

  // Función para obtener las tareas
  const fetchTasks = useCallback(async () => {
    try {
      const res = await axios.get("http://localhost:5001/tasks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error("Error al obtener tareas", err);
    }
  }, [token]);

  // Función para obtener los usuarios
  const fetchUsuarios = useCallback(async () => {
    try {
      const res = await axios.get("http://localhost:5001/auth/user", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsuarios(res.data);
    } catch (err) {
      console.error("Error al obtener usuarios", err);
    }
  }, [token]);

  // Cargar tareas y usuarios al montar el componente
  useEffect(() => {
    fetchTasks();
    fetchUsuarios();
  }, [fetchTasks, fetchUsuarios]);

  // Función para manejar el envío del formulario (crear o editar tarea)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingTaskId) {
        await axios.put(`http://localhost:5001/tasks/${editingTaskId}`, formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        Swal.fire("Editada", "Tarea actualizada correctamente", "success");
      } else {
        await axios.post("http://localhost:5001/tasks", formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        Swal.fire("Agregada", "Tarea creada correctamente", "success");
      }

      // Resetear el formulario y recargar las tareas
      setFormData({
        titulo: "",
        descripcion: "",
        fecha: "",
        prioridad: "",
        categoria: "",
        usuario: "",
      });
      setEditingTaskId(null);
      fetchTasks();
    } catch (err) {
      Swal.fire("Error", "No se pudo guardar la tarea", "error");
    }
  };

  // Función para editar tarea
  const handleEdit = (task) => {
    setFormData({
      titulo: task.titulo,
      descripcion: task.descripcion,
      fecha: task.fecha?.split("T")[0] || "",
      prioridad: task.prioridad,
      categoria: task.categoria,
      usuario: task.usuario?._id || "",
    });
    setEditingTaskId(task._id);
  };

  // Función para eliminar tarea
  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "¿Estás segura?",
      text: "No podrás deshacer esto",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
    });

    if (confirm.isConfirmed) {
      try {
        await axios.delete(`http://localhost:5001/tasks/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        fetchTasks();
        Swal.fire("Eliminada", "La tarea ha sido eliminada", "success");
      } catch (err) {
        Swal.fire("Error", "No se pudo eliminar la tarea", "error");
      }
    }
  };

  // Función para cambiar el estado de la tarea (completada/no completada)
  const toggleEstado = async (id, estadoActual) => {
    try {
      await axios.put(
        `http://localhost:5001/tasks/${id}/estado`,
        { estado: !estadoActual },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchTasks();
    } catch (err) {
      console.error("Error al cambiar estado", err);
    }
  };

  return (
    <div className="container my-5">
      <div className="container">
        <NavbarAdmin />
        <TaskForm
          handleSubmit={handleSubmit}
          formData={formData}
          setFormData={setFormData}
          editingTaskId={editingTaskId}
          usuarios={usuarios}
        />
        <TaskList
          tasks={tasks}
          handleEdit={handleEdit}
          handleDelete={handleDelete}
          toggleEstado={toggleEstado}
          isAdmin={true}
        />
      </div>
    </div>
  );
};

export default AdminTareas;
