import 'bootstrap/dist/css/bootstrap.min.css';
import "bootstrap/dist/js/bootstrap.bundle.min.js";

import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login"; 
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import AdminPanel from "./pages/AdminPanel";
import AdminRoute from "./components/AdminRoute";
import { AuthProvider, useAuth } from "./context/AuthContext";
import './index.css';

const AppRoutes = () => {
  const { token, user, loading } = useAuth();

  if (loading) return null; // puedes reemplazar con un spinner si quieres

  // Redirección según el rol del usuario
  const redireccionSegunRol = () => {
    if (!token) return <Navigate to="/login" />;
    if (user?.role === "admin") return <Navigate to="/admin" />;
    return <Navigate to="/dashboard" />;
  };

  return (
    <Routes>
      <Route path="/login" element={!token ? <Login /> : <Navigate to="/dashboard" />} />

      <Route path="/" element={redireccionSegunRol()} />
      <Route path="/login" element={redireccionSegunRol()} />

      <Route
        path="/register"
        element={
          token ? (
            <AdminRoute>
              <Register />
            </AdminRoute>
          ) : (
            <Navigate to="/login" />
          )
        }
      />

      <Route
        path="/dashboard"
        element={
          token && user?.role === "user" ? (
            <Dashboard />
          ) : (
            <Navigate to={token ? "/admin" : "/login"} />
          )
        }
      />

      <Route
        path="/admin"
        element={
          token && user?.role === "admin" ? (
            <AdminPanel />
          ) : (
            <Navigate to={token ? "/dashboard" : "/login"} />
          )
        }
      />

      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}

export default App;
