import { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";

const TaskForm = ({ tareaActual, onGuardar }) => {
  const { token, usuario } = useAuth();

  const [formData, setFormData] = useState({
    titulo: "",
    descripcion: "",
    completada: false,
    usuario: "",
  });

  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    if (tareaActual) {
      setFormData({
        titulo: tareaActual.titulo || "",
        descripcion: tareaActual.descripcion || "",
        completada: tareaActual.completada || false,
        usuario: tareaActual.usuario?._id || "",
      });
    } else {
      setFormData({
        titulo: "",
        descripcion: "",
        completada: false,
        usuario: "",
      });
    }
  }, [tareaActual]);

  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const res = await fetch("http://localhost:4000/api/auth/users", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const data = await res.json();
        if (Array.isArray(data)) {
          setUsuarios(data);
        } else {
          console.error("La respuesta no es un array de usuarios", data);
        }
      } catch (error) {
        console.error("Error al cargar usuarios:", error);
      }
    };

    if (usuario?.role === "admin") {
      fetchUsuarios();
    }
  }, [token, usuario]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.titulo.trim()) return alert("El título es obligatorio");
    onGuardar(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="card p-4 shadow">
      <h4>{tareaActual ? "Editar tarea" : "Nueva tarea"}</h4>

      <div className="mb-3">
        <label className="form-label">Título</label>
        <input
          type="text"
          name="titulo"
          className="form-control"
          value={formData.titulo}
          onChange={handleChange}
        />
      </div>

      <div className="mb-3">
        <label className="form-label">Descripción</label>
        <textarea
          name="descripcion"
          className="form-control"
          rows="3"
          value={formData.descripcion}
          onChange={handleChange}
        />
      </div>

      <div className="form-check mb-3">
        <input
          className="form-check-input"
          type="checkbox"
          name="completada"
          checked={formData.completada}
          onChange={handleChange}
        />
        <label className="form-check-label">Completada</label>
      </div>

      {usuario?.role === "admin" && (
        <div className="mb-3">
          <label className="form-label">Asignar a:</label>
          <select
            name="usuario"
            className="form-select"
            value={formData.usuario}
            onChange={handleChange}
          >
            <option value="">Selecciona un usuario</option>
            {usuarios.map((u) => (
              <option key={u._id} value={u._id}>
                {u.username} ({u.email})
              </option>
            ))}
          </select>
        </div>
      )}

      <button className="btn btn-primary" type="submit">
        Guardar tarea
      </button>
    </form>
  );
};

export default TaskForm;
