import { useEffect, useState } from "react";
import { FaUser, FaEdit, FaTrash } from "react-icons/fa";
import NavbarAdmin from "../components/NavbarAdmin";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Swal from "sweetalert2";

const AdminUsers = () => {
  const { token } = useAuth();
  const [usuarios, setUsuarios] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const res = await fetch("http://localhost:5001/auth/users1", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          throw new Error("Error al obtener usuarios");
        }

        const data = await res.json();
        setUsuarios(data);
      } catch (err) {
        console.error("Error al obtener usuarios", err);
        Swal.fire("Error", "No se pudieron obtener los usuarios", "error");
      }
    };

    fetchUsuarios();
  }, [token]);

  // Función para eliminar usuario con SweetAlert2
  const handleDelete = async (userId) => {
    const confirm = await Swal.fire({
      title: "¿Estás seguro?",
      text: "Esta acción no se puede deshacer",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
      cancelButtonText: "Cancelar",
    });

    if (confirm.isConfirmed) {
      try {
        const response = await fetch(`http://localhost:5001/auth/users1/${userId}`, {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error("Error al eliminar usuario");
        }

        setUsuarios(usuarios.filter((usuario) => usuario._id !== userId));
        Swal.fire("Eliminado", "El usuario ha sido eliminado", "success");
      } catch (err) {
        console.error("Error al eliminar usuario", err);
        Swal.fire("Error", "No se pudo eliminar el usuario", "error");
      }
    }
  };

  const handleEdit = (userId) => {
    navigate(`/admin/users/edit/${userId}`);
  };

  return (
    <div className="container">
      <NavbarAdmin />
      <div className="container my-5">
        <h2 className="text-center fw-bold mb-4">Usuarios Registrados</h2>
        <div className="list-group">
          {usuarios.map((u) => (
            <div
              key={u._id}
              className="list-group-item d-flex align-items-center gap-3"
            >
              <FaUser size={20} />
              <div className="flex-grow-1">
                <strong>{u.username}</strong> - {u.email} - Rol: {u.role}
              </div>
              <div>
                <button
                  className="btn btn-sm btn-primary ms-2"
                  onClick={() => handleEdit(u._id)}
                >
                  <FaEdit /> Editar
                </button>
                <button
                  className="btn btn-sm btn-danger ms-2"
                  onClick={() => handleDelete(u._id)}
                >
                  <FaTrash /> Eliminar
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminUsers;
