import { useEffect, useState, useCallback, useRef } from "react";
import TaskForm from "../components/TaskForm";
import TaskList from "../components/TaskList";
import NavbarAdmin from "../components/NavbarAdmin";
import { useAuth } from "../context/AuthContext";
import Swal from "sweetalert2";
import "../styles/AdminTareas.css"; // Importar los estilos CSS

const AdminTareas = () => {
  const { token } = useAuth();
  const [tasks, setTasks] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [formData, setFormData] = useState({
    titulo: "",
    descripcion: "",
    fecha: "",
    prioridad: "",
    categoria: "",
    usuario: "",
  });
  const formRef = useRef(null);
  const [editingTaskId, setEditingTaskId] = useState(null);
  const API_URL = "http://localhost:5001";

  const fetchTasks = useCallback(async () => {
    if (!token) return console.error("Token no disponible.");

    try {
      const response = await fetch(`${API_URL}/tasks`, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
      });

      if (response.ok) {
        const data = await response.json();
        setTasks(Array.isArray(data) ? data : []);
      } else {
        console.error("Error al obtener tareas:", response.statusText);
      }
    } catch (err) {
      console.error("Error al obtener tareas", err);
    }
  }, [token]);

  const fetchUsuarios = useCallback(async () => {
    if (!token) return console.error("Token no disponible.");

    try {
      const response = await fetch(`${API_URL}/auth/user`, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
      });

      if (response.ok) {
        const data = await response.json();
        setUsuarios(data);
      } else {
        console.error("Error al obtener usuarios:", response.statusText);
      }
    } catch (err) {
      console.error("Error al obtener usuarios", err);
    }
  }, [token]);

  useEffect(() => {
    if (token) {
      fetchTasks();
      fetchUsuarios();
    } else {
      console.error("Token no disponible.");
    }
  }, [fetchTasks, fetchUsuarios, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.usuario) {
      Swal.fire("Error", "Debes seleccionar un usuario para asignar la tarea", "error");
      return;
    }

    try {
      const method = editingTaskId ? "PUT" : "POST";
      const url = editingTaskId ? `${API_URL}/tasks/${editingTaskId}` : `${API_URL}/tasks`;

      const response = await fetch(url, {
        method,
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        Swal.fire(editingTaskId ? "Tarea actualizada" : "Tarea creada", "Operación exitosa", "success");
        setFormData({ titulo: "", descripcion: "", fecha: "", prioridad: "", categoria: "", usuario: "" });
        setEditingTaskId(null);
        fetchTasks();
      } else {
        Swal.fire("Error", "No se pudo guardar la tarea", "error");
      }
    } catch (err) {
      Swal.fire("Error", "No se pudo guardar la tarea", "error");
    }
  };

  const handleEdit = (task) => {
    setFormData({
      titulo: task.titulo,
      descripcion: task.descripcion,
      fecha: task.fecha?.split("T")[0] || "",
      prioridad: task.prioridad,
      categoria: task.categoria,
      usuario: task.usuario?._id || "",
    });
    setEditingTaskId(task._id);
    formRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "¿Estás seguro?",
      text: "No podrás deshacer esto",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
    });

    if (confirm.isConfirmed) {
      try {
        const response = await fetch(`${API_URL}/tasks/${id}`, {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" },
        });

        if (response.ok) {
          fetchTasks();
          Swal.fire("Eliminada", "La tarea ha sido eliminada", "success");
        } else {
          Swal.fire("Error", "No se pudo eliminar la tarea", "error");
        }
      } catch (err) {
        Swal.fire("Error", "No se pudo eliminar la tarea", "error");
      }
    }
  };

  return (
    <div className="admin-container">
      <NavbarAdmin />
      <div className="form-container" ref={formRef}>
        <TaskForm handleSubmit={handleSubmit} formData={formData} setFormData={setFormData} editingTaskId={editingTaskId} usuarios={usuarios} />
        <TaskList tasks={tasks} handleEdit={handleEdit} handleDelete={handleDelete} isAdmin={true} />
      </div>
    </div>
  );
};

export default AdminTareas;
