import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const AdminUsers = () => {
  const [usuarios, setUsuarios] = useState([]);
  const { token } = useContext(AuthContext); // <-- asegurarte de que tienes el token del contexto

  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const res = await fetch("http://localhost:5001/auth/users1", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // <-- Token aquí
          },
        });

        if (!res.ok) {
          throw new Error("Error al obtener usuarios");
        }

        const data = await res.json();
        setUsuarios(data);
      } catch (error) {
        console.error("Error al obtener usuarios", error);
      }
    };

    fetchUsuarios();
  }, [token]);

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Lista de Usuarios</h2>
      <div className="row">
        {usuarios.map((user) => (
          <div className="col-md-4 mb-3" key={user._id}>
            <Card>
              <CardContent>
                <h5>{user.username}</h5>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Rol:</strong> {user.rol}</p>
              </CardContent>
            </Card>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminUsers;
