import { useEffect, useState } from "react";
import { FaUser, FaEdit, FaTrash } from "react-icons/fa";

import NavbarAdmin from "../components/NavbarAdmin";
import { useAuth } from "../context/AuthContext";
import Swal from "sweetalert2";
import "../styles/AdminUsers.css";


const AdminUsers = () => {
  const { token } = useAuth();
  const [usuarios, setUsuarios] = useState([]);

  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const res = await fetch("http://localhost:5001/auth/users1", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) throw new Error("Error al obtener usuarios");

        const data = await res.json();
        setUsuarios(data);
      } catch (err) {
        console.error("Error al obtener usuarios", err);
        Swal.fire("Error", "No se pudieron obtener los usuarios", "error");
      }
    };

    fetchUsuarios();
  }, [token]);

  const handleDelete = async (userId) => {
    const confirm = await Swal.fire({
      title: "¿Estás seguro?",
      text: "Esta acción no se puede deshacer",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Sí, eliminar",
      cancelButtonText: "Cancelar",
      customClass: {
        confirmButton: "btn btn-danger px-4 py-2 rounded-pill fw-bold",
        cancelButton: "btn btn-secondary px-4 py-2 rounded-pill fw-bold ms-2",
      },
      buttonsStyling: false,
    });

    if (confirm.isConfirmed) {
      try {
        const response = await fetch(`http://localhost:5001/auth/users1/${userId}`, {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) throw new Error("Error al eliminar usuario");

        setUsuarios(usuarios.filter((usuario) => usuario._id !== userId));
        Swal.fire("Eliminado", "El usuario ha sido eliminado", "success");
      } catch (err) {
        console.error("Error al eliminar usuario", err);
        Swal.fire("Error", "No se pudo eliminar el usuario", "error");
      }
    }
  };

  const handleEdit = async (userId) => {
    const usuario = usuarios.find((u) => u._id === userId);

    const { value: formValues } = await Swal.fire({
      title: '<h3 class="fw-bold mb-4 text-info">Editar Usuario</h3>',
      html: `
        <div class="text-start">
          <label class="form-label fw-semibold text-muted">Nombre de usuario</label>
          <input id="swal-input1" class="form-control mb-3 rounded-3 shadow-lg" value="${usuario.username}" />

          <label class="form-label fw-semibold text-muted">Correo electrónico</label>
          <input id="swal-input2" type="email" class="form-control mb-3 rounded-3 shadow-lg" value="${usuario.email}" />

          <label class="form-label fw-semibold text-muted">Nueva contraseña</label>
          <input id="swal-input3" type="password" class="form-control mb-2 rounded-3 shadow-lg" placeholder="(Dejar en blanco para no cambiar)" />
        </div>
      `,
      showCancelButton: true,
      confirmButtonText: '<i class="fa fa-check me-2"></i>Guardar cambios',
      cancelButtonText: '<i class="fa fa-times me-2"></i>Cancelar',
      focusConfirm: false,
      customClass: {
        popup: 'border-0 rounded-4 shadow-lg p-5 bg-white',
        confirmButton: 'btn btn-success px-4 py-2 rounded-pill fw-bold shadow-lg transition-all duration-300',
        cancelButton: 'btn btn-outline-secondary px-4 py-2 rounded-pill fw-bold shadow-lg transition-all duration-300',
        input: 'transition-all duration-300 ease-in-out focus:outline-none focus:ring-2 focus:ring-blue-500',
      },
      buttonsStyling: false,
      preConfirm: () => {
        const username = document.getElementById("swal-input1").value.trim();
        const email = document.getElementById("swal-input2").value.trim();
        const password = document.getElementById("swal-input3").value;

        if (!username || !email) {
          Swal.showValidationMessage("Username y Email son obligatorios");
          return false;
        }

        return { username, email, password };
      },
    });

    if (formValues) {
      try {
        const response = await fetch(`http://localhost:5001/auth/users1/${userId}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(formValues),
        });

        if (!response.ok) throw new Error("Error al actualizar usuario");

        setUsuarios((prev) =>
          prev.map((u) => (u._id === userId ? { ...u, ...formValues } : u))
        );

        Swal.fire({
          icon: "success",
          title: "Usuario actualizado",
          text: "Los cambios se guardaron correctamente",
          confirmButtonColor: "#198754",
        });
      } catch (err) {
        console.error(err);
        Swal.fire("Error", "No se pudo actualizar el usuario", "error");
      }
    }
  };

  return (
    <div className="container">
      <NavbarAdmin />
      <div className="container my-5">
        <h2 className="text-center fw-bold mb-4">Usuarios Registrados</h2>
        <div className="row">
          {usuarios.map((u) => (
            <div key={u._id} className="col-md-4 mb-4">
              <div className="card p-3 shadow-sm">
                <div className="d-flex justify-content-between align-items-center">
                  <FaUser size={50} />
                  <div className="d-flex flex-column align-items-start ms-3">
                    <strong>{u.username}</strong>
                    <span>{u.email}</span>
                    <span>Rol: {u.role}</span>
                  </div>
                  <div className="d-flex gap-2">
                    <button
                      className="btn btn-sm"
                      onClick={() => handleEdit(u._id)}
                    >
                      <FaEdit /> Editar
                    </button>
                    <button
                      className="btn-delete btn-sm "
                      onClick={() => handleDelete(u._id)}
                    >
                      <FaTrash /> Eliminar
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminUsers;
